import { GoogleLogin }  from 'react-google-login';
import Fbportal from './feedback_portal';

const clientId = "925306536966-8krnsg2kdor08se5s6d47vla1pec5f2f.apps.googleusercontent.com";

const Login = () =>{

  const onSuccess = (res) =>{
     console.log("LOGIN SUCCESS! Current user: ", res.profileObj);
     localStorage.setItem ("userid", res.profileObj.googleId);
     // here we store login details in the chrome local storage so that we can login
     localStorage.setItem("fullname", res.profileObj.name);
     window.location.href="http://localhost:3000/#/"
   window.location.reload();// Reload the page(app.js) after login is success
    
  }

  const onFailure = (res) =>{
    alert("login failed! try again...")
  }

  const googleLoginCallback = (response) => {
    // handle JWT token inside response...
    // https://developers.google.com/identity/gsi/web/guides/handle-credential-responses-js-functions#handle_credential_response
    };
    

    const createFakeGoogleWrapper = () => {
      const googleLoginWrapper = document.createElement("div");
      // Or you can simple hide it in CSS rule for custom-google-button
      googleLoginWrapper.style.display = "none";
      googleLoginWrapper.classList.add("custom-google-button");
    
      // Add the wrapper to body
      document.body.appendChild(googleLoginWrapper);
      
      // Use GSI javascript api to render the button inside our wrapper
      // You can ignore the properties because this button will not appear
      window.google.accounts.id.renderButton(googleLoginWrapper, {
        type: "icon",
        width: "200",
      });
    
      const googleLoginWrapperButton =
        googleLoginWrapper.querySelector("div[role=button]");
    
      return {
        click: () => {
          googleLoginWrapperButton.click();
        },
      };
    };
    
    // Now we have a wrapper to click
    const googleButtonWrapper = createFakeGoogleWrapper();
    
    // https://developers.google.com/identity/gsi/web/reference/js-reference#IdConfiguration
    window.google.accounts.id.initialize({
      client_id: YOUR_GOOGLE_CLIENT_ID,
      ux_mode: "popup",
      callback: googleLoginCallback,
    });

  return(
    <section className='text-center edit' >
    <nav className="navbar bg-body-tertiary bg-dark  p-4 h1">
    <div className="container-fluid">
    <span className="navbar-brand mb-0 h1">Feedback Portal</span>
    </div>
  </nav>


  <div className="container  mt-5 mb-5" >
                        <div className="row p-5" >
                             <div className="col-lg-6 p-5">
                              <div>
                              <p className='title'><b>feedback</b></p>
                               <p className='subtitle '> <b>Feedback is the compass for greatness; it tells you what to avoid, what to learn, and where to excel</b></p>
                               </div>
                             </div>
                            <div className="col-lg-4">
                            <div className="p-4 shadow-lg login-box mt-4">

                                 <div className='row'>
                                   <h2 className="text-center">Login with google</h2>
                                     <hr/>
                                    <div className='mb-3 pt-4'>
                                       <p> <div id="signInButton" >
                                        <GoogleLogin 
                                          clientId = {clientId}
                                          buttonText = "Login in google"
                                          onSuccess = {onSuccess}
                                          onFailure = {onFailure}
                                          cookiePolicy={'single_host_origin'}
                                        />
                                      </div></p>
                                      
                                       </div>
                                 </div>
                            </div>
                           
                         </div>
                         <div className="col-lg-2"></div>
                      </div>
                      </div>
    
     
    
  </section>
  )
}

export default Login;
