import { useState, useEffect } from "react";

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState("create");
  const [rules, setRules] = useState([]);
  const [selectedRule, setSelectedRule] = useState("");
  const [age, setAge] = useState("");
  const [department, setDepartment] = useState("");
  const [salary, setSalary] = useState("");
  const [experience, setExperience] = useState("");
  const [error, setError] = useState("");
  const [output, setOutput] = useState("");

  const fetchRules = () => {
    fetch("http://localhost:5556/getRules")
      .then((response) => response.json())
      .then((rulesArray) => {
        setRules(rulesArray);
      })
      .catch((error) => {
        console.error("Error fetching rules:", error);
      });
  };

  const handleEvaluate = () => {
    let hasError = false;

    if (!age || isNaN(age)) {
      setError("Age must be a valid number.");
      hasError = true;
    } else if (!department) {
      setError("Department is required.");
      hasError = true;
    } else if (!salary || isNaN(salary)) {
      setError("Salary must be a valid number.");
      hasError = true;
    } else if (!experience || isNaN(experience)) {
      setError("Experience must be a valid number.");
      hasError = true;
    } else if (!selectedRule) {
      setError("Please select a rule to evaluate.");
      hasError = true;
    } else {
      setError("");
    }

    if (!hasError) {
      const data = { age, department, salary, experience, rule: selectedRule };
      fetch("http://localhost:5556/evaluateRule", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      })
        .then((response) => response.json())
        .then((result) => {
          setOutput(result.isEligible ? "True (User is eligible)" : "False (User is not eligible)");
        })
        .catch((error) => {
          console.error("Error evaluating rule:", error);
          setOutput("Error evaluating rule.");
        });
    }
  };

  useEffect(() => {
    if (activeTab === "evaluate") {
      fetchRules();
    }
  }, [activeTab]);

  return (
    <section>
      <div className="container">
        <div className="row mt-5">
          <div className="col-lg-12 text-center fs-1 fw-bold text-dark">
            Rules & Eligibility
          </div>
        </div>
        <div className="row">
          <div className="col-lg-12 p-5 text-center">
            <div className="btn-group">
              <button
                className={`btn btn-outline-dark fw-bold ${activeTab === "create" ? "active" : ""}`}
                onClick={() => setActiveTab("create")}
              >
                Create Rule
              </button>
              <button
                className={`btn btn-outline-dark fw-bold ${activeTab === "combine" ? "active" : ""}`}
                onClick={() => {
                  setActiveTab("combine");
                  fetchRules();
                }}
              >
                Combine Rules
              </button>
              <button
                className={`btn btn-outline-dark fw-bold ${activeTab === "evaluate" ? "active" : ""}`}
                onClick={() => setActiveTab("evaluate")}
              >
                Evaluate Rules
              </button>
            </div>
          </div>
        </div>
        <div className="row mb-5">
          <div className="container p-5 feedback-box">
            <div className="row">
              <h3 className="text-center">
                {activeTab === "create" && "Create Rule"}
                {activeTab === "combine" && "Combine Rules"}
                {activeTab === "evaluate" && "Evaluate Rules"}
              </h3>
            </div>

            <div className="row">
              <div className="col-lg-3"></div>
              <div className="col-lg-6">
                {activeTab === "create" && (
                  <form>
                    <textarea className="form-control mb-3" placeholder="Enter Rule"></textarea>
                    <button type="submit" className="btn btn-primary">Create Rule</button>
                  </form>
                )}

                {activeTab === "combine" && (
                  <div>
                    {rules.length > 0 ? (
                      <form>
                        {rules.map((rule, index) => (
                          <div key={index} className="form-check">
                            <input
                              className="form-check-input"
                              type="checkbox"
                              value={rule}
                              onChange={() => handleRuleSelection(rule)}
                            />
                            <label className="form-check-label">
                              {rule}
                            </label>
                          </div>
                        ))}
                        <button
                          type="button"
                          className="btn btn-primary mt-3"
                          onClick={handleCombineRules}
                        >
                          Combine Selected Rules
                        </button>
                      </form>
                    ) : (
                      <p>No rules available. Click the button to fetch rules.</p>
                    )}
                  </div>
                )}

                {activeTab === "evaluate" && (
                  <form>
                    {rules.length > 0 ? (
                      <div>
                        {rules.map((rule, index) => (
                          <div key={index} className="form-check">
                            <input
                              className="form-check-input"
                              type="radio"
                              name="rule"
                              value={rule}
                              onChange={() => setSelectedRule(rule)}
                            />
                            <label className="form-check-label">
                              {rule}
                            </label>
                          </div>
                        ))}
                      </div>
                    ) : (
                      <p>No rules available. Click the button to fetch rules.</p>
                    )}

                    <div className="form-group mb-3">
                      <label>Age:</label>
                      <input
                        type="number"
                        className="form-control"
                        value={age}
                        onChange={(e) => setAge(e.target.value)}
                        style={{ borderColor: error && !age ? "red" : "" }}
                      />
                    </div>
                    <div className="form-group mb-3">
                      <label>Department:</label>
                      <input
                        type="text"
                        className="form-control"
                        value={department}
                        onChange={(e) => setDepartment(e.target.value)}
                        style={{ borderColor: error && !department ? "red" : "" }}
                      />
                    </div>
                    <div className="form-group mb-3">
                      <label>Salary:</label>
                      <input
                        type="number"
                        className="form-control"
                        value={salary}
                        onChange={(e) => setSalary(e.target.value)}
                        style={{ borderColor: error && !salary ? "red" : "" }}
                      />
                    </div>
                    <div className="form-group mb-3">
                      <label>Experience:</label>
                      <input
                        type="number"
                        className="form-control"
                        value={experience}
                        onChange={(e) => setExperience(e.target.value)}
                        style={{ borderColor: error && !experience ? "red" : "" }}
                      />
                    </div>
                    {error && <p style={{ color: "red" }}>{error}</p>}
                    <button
                      type="button"
                      className="btn btn-primary"
                      onClick={handleEvaluate}
                    >
                      Evaluate
                    </button>
                  </form>
                )}
              </div>
              <div className="col-lg-3"></div>
            </div>
          </div>
        </div>
        {output && (
          <div className="row mt-3">
            <div className="col-lg-12 text-center">
              <p>Output: {output}</p>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default Dashboard;
