import { useState, useEffect } from "react";

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState("create");
  const [rules, setRules] = useState([]);
  const [selectedRules, setSelectedRules] = useState([]);
  const [rule, setRule] = useState("");
  const [age, setAge] = useState("");
  const [department, setDepartment] = useState("");
  const [salary, setSalary] = useState("");
  const [experience, setExperience] = useState("");
  const [errors, setErrors] = useState({
    rule: "",
    age: "",
    department: "",
    salary: "",
    experience: "",
    selectedRule: "",
  });
  const [selectedRule, setSelectedRule] = useState("");

  useEffect(() => {
    if (activeTab === "evaluate") {
      fetchRules();
    }
  }, [activeTab]);

  const fetchRules = () => {
    fetch("http://localhost:5556/rules")
      .then((response) => response.json())
      .then((rulesArray) => {
        setRules(rulesArray);
      })
      .catch((error) => {
        console.error("Error fetching rules:", error);
      });
  };

  const handleRuleSelection = (rule) => {
    setSelectedRule(rule);
  };

  const handleCombineRules = () => {
    console.log("Combining rules:", selectedRules);
    alert("Rules combined successfully!");
  };

  const validateRule = () => {
    let valid = true;
    let newErrors = { ...errors };

    if (rule.trim() === "") {
      valid = false;
      newErrors.rule = "Rule cannot be empty.";
    } else if (rule.length < 50) {
      valid = false;
      newErrors.rule = "Rule must be at least 50 characters long.";
    } else if (!/AND|OR/.test(rule)) {
      valid = false;
      newErrors.rule = "Rule must contain at least one 'AND' or 'OR' operator.";
    } else {
      newErrors.rule = "";
    }

    setErrors(newErrors);

    if (valid) {
      submitRule();
    }
  };

  const submitRule = () => {
    const url = "http://localhost:5556/rules";
    const newRule = {
      rule: rule,
    };

    const postdata = {
      headers: { "Content-type": "application/json" },
      method: "POST",
      body: JSON.stringify(newRule),
    };

    fetch(url, postdata)
      .then((response) => response.json())
      .then((data) => {
        alert("Rule Added Successfully!");
        setRule("");
      })
      .catch((error) => {
        console.error("Error submitting rule:", error);
      });
  };

  const validateEvaluation = () => {
    let valid = true;
    let newErrors = { ...errors };

    if (age.trim() === "" || isNaN(age)) {
      valid = false;
      newErrors.age = "Age must be a number and cannot be empty.";
    } else {
      newErrors.age = "";
    }

    if (department.trim() === "") {
      valid = false;
      newErrors.department = "Department cannot be empty.";
    } else {
      newErrors.department = "";
    }

    if (salary.trim() === "" || isNaN(salary)) {
      valid = false;
      newErrors.salary = "Salary must be a number and cannot be empty.";
    } else {
      newErrors.salary = "";
    }

    if (experience.trim() === "" || isNaN(experience)) {
      valid = false;
      newErrors.experience = "Experience must be a number and cannot be empty.";
    } else {
      newErrors.experience = "";
    }

    if (!selectedRule) {
      valid = false;
      newErrors.selectedRule = "Please select a rule.";
    } else {
      newErrors.selectedRule = "";
    }

    setErrors(newErrors);

    if (valid) {
      submitEvaluation();
    }
  };

  const submitEvaluation = () => {
    const url = "http://localhost:5556/evaluateRule";
    const evaluationData = {
      age,
      department,
      salary,
      experience,
      rule: selectedRule,
    };

    const postdata = {
      headers: { "Content-type": "application/json" },
      method: "POST",
      body: JSON.stringify(evaluationData),
    };

    fetch(url, postdata)
      .then((response) => response.json())
      .then((data) => {
        alert(`Evaluation Result: ${data.result ? "True" : "False"}`);
      })
      .catch((error) => {
        console.error("Error evaluating rule:", error);
      });
  };

  return (
    <section>
      <div className="container">
        <div className="row mt-5">
          <div className="col-lg-12 text-center fs-1 fw-bold text-dark">
            Rules & Eligibility
          </div>
        </div>
        <div className="row">
          <div className="col-lg-12 p-5 text-center">
            <div className="btn-group">
              <button
                className={`btn btn-outline-dark fw-bold ${activeTab === "create" ? "active" : ""}`}
                onClick={() => setActiveTab("create")}
              >
                Create Rule
              </button>
              <button
                className={`btn btn-outline-dark fw-bold ${activeTab === "combine" ? "active" : ""}`}
                onClick={() => {
                  setActiveTab("combine");
                  fetchRules();
                }}
              >
                Combine Rules
              </button>
              <button
                className={`btn btn-outline-dark fw-bold ${activeTab === "evaluate" ? "active" : ""}`}
                onClick={() => setActiveTab("evaluate")}
              >
                Evaluate Rules
              </button>
            </div>
          </div>
        </div>
        <div className="row mb-5">
          <div className="container p-5 feedback-box">
            <div className="row">
              <h3 className="text-center">
                {activeTab === "create" && "Create Rule"}
                {activeTab === "combine" && "Combine Rules"}
                {activeTab === "evaluate" && "Evaluate Rules"}
              </h3>
            </div>

            <div className="row">
              <div className="col-lg-3"></div>
              <div className="col-lg-6">
                {activeTab === "create" && (
                  <form>
                    <textarea
                      className={`form-control mb-3 ${errors.rule ? "is-invalid" : ""}`}
                      placeholder="Enter Rule"
                      value={rule}
                      onChange={(e) => setRule(e.target.value)}
                      rows={8}
                      style={{ borderColor: errors.rule ? "red" : "" }}
                    ></textarea>
                    {errors.rule && <small className="text-danger fst-italic"><i>{errors.rule}</i></small>}
                    <div className='text-center'>
                      <button
                        type="button"
                        className="btn btn-primary mt-3 bg-dark"
                        onClick={validateRule}
                      >
                        Create Rule
                      </button>
                    </div>
                  </form>
                )}

                {activeTab === "combine" && (
                  <div>
                    {rules.length > 0 ? (
                      <form>
                        {rules.map((rule, index) => (
                        <div key={index} className="form-check">
                          <input
                            className="form-check-input"
                            type="checkbox"
                            value={rule._id}
                            onChange={() => handleRuleSelection(rule.rule)}
                          />
                          <label className="form-check-label">
                            {rule.rule}
                          </label>
                        </div>
                      ))}

                       <div className='text-center'>
                        <button
                          type="button"
                          className="btn btn-primary mt-3"
                          onClick={handleCombineRules}
                        >
                          Combine Selected Rules
                        </button>
                        </div>
                      </form>
                    ) : (
                      <p>No rules available. Click the button to fetch rules.</p>
                    )}
                  </div>
                )}

                {activeTab === "evaluate" && (
                  <form>
                    <div className="mb-3">
                      <label className="form-label">Age</label>
                      <input
                        type="number"
                        className={`form-control ${errors.age ? "is-invalid" : ""}`}
                        value={age}
                        onChange={(e) => setAge(e.target.value)}
                        style={{ borderColor: errors.age ? "red" : "" }}
                      />
                      {errors.age && <small className="text-danger fst-italic"><i>{errors.age}</i></small>}
                    </div>
                    <div className="mb-3">
                      <label className="form-label">Department</label>
                      <input
                        type="text"
                        className={`form-control ${errors.department ? "is-invalid" : ""}`}
                        value={department}
                        onChange={(e) => setDepartment(e.target.value)}
                        style={{ borderColor: errors.department ? "red" : "" }}
                      />
                      {errors.department && <small className="text-danger fst-italic"><i>{errors.department}</i></small>}
                    </div>
                    <div className="mb-3">
                      <label className="form-label">Salary</label>
                      <input
                        type="number"
                        className={`form-control ${errors.salary ? "is-invalid" : ""}`}
                        value={salary}
                        onChange={(e) => setSalary(e.target.value)}
                        style={{ borderColor: errors.salary ? "red" : "" }}
                      />
                      {errors.salary && <small className="text-danger fst-italic"><i>{errors.salary}</i></small>}
                    </div>
                    <div className="mb-3">
                      <label className="form-label">Experience</label>
                      <input
                        type="number"
                        className={`form-control ${errors.experience ? "is-invalid" : ""}`}
                        value={experience}
                        onChange={(e) => setExperience(e.target.value)}
                        style={{ borderColor: errors.experience ? "red" : "" }}
                      />
                      {errors.experience && <small className="text-danger fst-italic"><i>{errors.experience}</i></small>}
                    </div>
                    {errors.selectedRule && <small className="text-danger fst-italic"><i>{errors.selectedRule}</i></small>}
                    <div className='text-center'>
                    <button
                      type="button"
                      className="btn btn-primary mt-3 bg-dark"
                      onClick={validateEvaluation}
                    >
                      Evaluate
                    </button>
                    </div>
                  </form>
                )}
              </div>
              <div className="col-lg-3"></div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Dashboard;
