import { useState } from "react";

const Dashboard = () => {
  const [activeTab, setActiveTab] = useState("create");
  const [rules, setRules] = useState([]);
  const [selectedRules, setSelectedRules] = useState([]);
  const [rule, setRule] = useState("");
  const [error, setError] = useState("");

  const fetchRules = () => {
    fetch("http://localhost:5556/getRules")
      .then((response) => response.json())
      .then((rulesArray) => {
        setRules(rulesArray);
      })
      .catch((error) => {
        console.error("Error fetching rules:", error);
      });
  };

  const handleRuleSelection = (rule) => {
    if (selectedRules.includes(rule)) {
      setSelectedRules(selectedRules.filter((r) => r !== rule));
    } else {
      setSelectedRules([...selectedRules, rule]);
    }
  };

  const handleCombineRules = () => {
    console.log("Combining rules:", selectedRules);
    alert("Rules combined successfully!");
  };

  const validateRule = () => {
    let valid = true;
    let errorMessage = "";

    if (rule.trim() === "") {
      valid = false;
      errorMessage = "Rule cannot be empty.";
    } else if (rule.length < 50) {
      valid = false;
      errorMessage = "Rule must be at least 50 characters long.";
    } else if (!/AND|OR/.test(rule)) {
      valid = false;
      errorMessage = "Rule must contain at least one 'AND' or 'OR' operator.";
    }

    if (!valid) {
      setError(errorMessage);
    } else {
      setError("");
      submitRule();
    }
  };

  const submitRule = () => {
    const url = "http://localhost:5556/submitRule";
    const newRule = {
      rule: rule,
    };

    const postdata = {
      headers: { "Content-type": "application/json" },
      method: "POST",
      body: JSON.stringify(newRule),
    };

    fetch(url, postdata)
      .then((response) => response.json())
      .then((data) => {
        alert("Rule Added Successfully!");
        setRule("");
      })
      .catch((error) => {
        console.error("Error submitting rule:", error);
      });
  };

  return (
    <section>
      <div className="container">
        <div className="row mt-5">
          <div className="col-lg-12 text-center fs-1 fw-bold text-dark">
            Rules & Eligibility
          </div>
        </div>
        <div className="row">
          <div className="col-lg-12 p-5 text-center">
            <div className="btn-group text-center bg-dark ">
              <button
                className={`btn btn-outline-dark fw-bold ${activeTab === "create" ? "active" : ""}`}
                onClick={() => setActiveTab("create")}
              >
                Create Rule
              </button>
              <button
                className={`btn btn-outline-dark fw-bold ${activeTab === "combine" ? "active" : ""}`}
                onClick={() => {
                  setActiveTab("combine");
                  fetchRules();
                }}
              >
                Combine Rules
              </button>
              <button
                className={`btn btn-outline-dark fw-bold ${activeTab === "evaluate" ? "active" : ""}`}
                onClick={() => setActiveTab("evaluate")}
              >
                Evaluate Rules
              </button>
            </div>
          </div>
        </div>
        <div className="row mb-5">
          <div className="container p-5 feedback-box">
            <div className="row">
              <h3 className="text-center">
                {activeTab === "create" && "Create Rule"}
                {activeTab === "combine" && "Combine Rules"}
                {activeTab === "evaluate" && "Evaluate Rules"}
              </h3>
            </div>

            <div className="row">
              <div className="col-lg-3"></div>
              <div className="col-lg-6">
                {activeTab === "create" && (
                  <form>
                    <textarea
                      className={`form-control mb-3 ${error ? "is-invalid" : ""}`}
                      placeholder="Enter Rule"
                      value={rule}
                      onChange={(e) => setRule(e.target.value)}
                      style={{ borderColor: error ? "red" : "" }}
                    ></textarea>
                    {error && <small className="text-danger fst-italic">{error}</small>}
                    <button
                      type="button"
                      className="btn btn-primary mt-3"
                      onClick={validateRule}
                    >
                      Create Rule
                    </button>
                  </form>
                )}

                {activeTab === "combine" && (
                  <div>
                    {rules.length > 0 ? (
                      <form>
                        {rules.map((rule, index) => (
                          <div key={index} className="form-check">
                            <input
                              className="form-check-input"
                              type="checkbox"
                              value={rule}
                              onChange={() => handleRuleSelection(rule)}
                            />
                            <label className="form-check-label">
                              {rule}
                            </label>
                          </div>
                        ))}
                        <button
                          type="button"
                          className="btn btn-primary mt-3"
                          onClick={handleCombineRules}
                        >
                          Combine Selected Rules
                        </button>
                      </form>
                    ) : (
                      <p>No rules available. Click the button to fetch rules.</p>
                    )}
                  </div>
                )}

                {activeTab === "evaluate" && (
                  <form>
                    <textarea className="form-control mb-3" placeholder="Enter Data to Evaluate"></textarea>
                    <button type="submit" className="btn btn-primary">Evaluate Rule</button>
                  </form>
                )}
              </div>
              <div className="col-lg-3"></div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Dashboard;
