      fetchRules();
    }
  }, [activeTab]);


  const fetchRules = () => {
    fetch("http://localhost:5556/rules")
      .then((response) => response.json())
      .then((rulesArray) => {
        setRules(rulesArray);
      })
      .catch((error) => {
        console.error("Error fetching rules:", error);
      });
  };



// combine rule section
  const validate_combine_Rules = () => {
    let valid = true;
    let newErrors = "";

    if (rulegroup.trim() === "") {
      valid = false;
      newErrors = "Rule cannot be empty.";
    } else if (rulegroup.length < 50) {
      valid = false;
      newErrors= "Rule must be at least 50 characters long.";
    } else if (!/AND|OR/.test(rulegroup)) {
      valid = false;
      newErrors = "Rule must contain at least one 'AND' or 'OR' operator.";
    } else {
      newErrors= "";
    }

    setgroupErrors(newErrors);

    if (valid) {
      if (rulegroup.trim() === "") {
        setgroupErrors({ rule: "Please enter at least one rule." });
        return;
      }
    
      // Split the rules by commas and trim spaces
      const rulesArray = rulegroup.split(",").map((r) => r.trim());
  
      // Update the selectedRules state with the trimmed rules
      // console.log(rulesArray[0]);
      //   console.log(rulesArray[1]);
    //  setSelectedRules(rulesArray);
      console.log(rulesArray);
      submitRules(rulesArray);
    }
  };




  // const validateRule = () => {
  //   let valid = true;
  //   let newErrors = { ...errors };
  
  //   if (rulegroup.trim() === "") {
  //     valid = false;
  //     newErrors.rule = "Rule cannot be empty.";
  //   } else if (rulegroup.length < 50) {
  //     valid = false;
  //     newErrors.rule = "Rule must be at least 50 characters long.";
  //   } else if (!/AND|OR/.test(rulegroup)) {
  //     valid = false;
  //     newErrors.rule = "Rule must contain at least one 'AND' or 'OR' operator.";
  //   } else {
  //     newErrors.rule = "";
  //   }
  
  //   setErrors(newErrors);
  
  //   if (valid) {
  //     // Replace previous rules with the new rulegroup
     
  //     let temp = [rulegroup];
  //     console.log(selectedRules);
  //     setSelectedRules(temp);

  //     console.log(selectedRules);
  //     submitRules(); // Call submitRules function to send the new rulegroup
  //   }
  // };
  

  const validateRule = () => {
    let valid = true;
    let newErrors = { ...errors };
  
    if (rulegroup.trim() === "") {
      valid = false;
      newErrors.rule = "Rule cannot be empty.";
    } else if (rulegroup.length < 50) {
      valid = false;
      newErrors.rule = "Rule must be at least 50 characters long.";
    } else if (!/AND|OR/.test(rulegroup)) {
      valid = false;
      newErrors.rule = "Rule must contain at least one 'AND' or 'OR' operator.";
    } else {
      newErrors.rule = "";
    }
  
    setErrors(newErrors);
  
    if (valid) {
      // Replace previous rules with the new rulegroup
      // setSelectedRules(() => [rulegroup]);
  
      // Using the updated state value for submission
      // No need to log selectedRules here as it will not reflect the updated state immediately
      submitRules(); // Call submitRules function to send the new rulegroup
    }
  };

  
  const submitRules = () => {
    const url = "http://localhost:5556/rules";
    //console.log(selectedRules);
    const newRule = {
      rule: selectedRules
    };

    const postdata = {
      headers: { "Content-type": "application/json" },
      method: "POST",
      body: JSON.stringify(newRule),
    };

    fetch(url, postdata)
      .then((response) => response.json())
      .then((data) => {
        alert("Rule Added Successfully!");
        setSelectedRules([]);
      })
      .catch((error) => {
        console.error("Error submitting rule:", error);
      });
  };

  const handleRuleSelection = (index) => {
    setSelectedRuleIndex(index);
  };
  
  
  // evalution section 

  const validateEvaluation = () => {
    let valid = true;
    let newErrors = { ...errors };

    if (age.trim() === "" || isNaN(age)) {
      valid = false;
      newErrors.age = "Age must be a number and cannot be empty.";
    } else {
      newErrors.age = "";
    }

    if (department.trim() === "") {
      valid = false;
      newErrors.department = "Department cannot be empty.";
    } else {
      newErrors.department = "";
    }

    if (salary.trim() === "" || isNaN(salary)) {
      valid = false;
      newErrors.salary = "Salary must be a number and cannot be empty.";
    } else {
      newErrors.salary = "";
    }

    if (experience.trim() === "" || isNaN(experience)) {
      valid = false;
      newErrors.experience = "Experience must be a number and cannot be empty.";
    } else {
      newErrors.experience = "";
    }

    if (selectedRuleIndex == null) {
      valid = false;
      newErrors.selectedRule = "Please select a rule.";
    } else {
      newErrors.selectedRule = "";
    }

    setErrors(newErrors);

    if (valid) {
      getAST();
    }
  };

  const getAST = () => {
    // Ensure a rule is selected
    if (selectedRuleIndex === null) {
      alert("Please select a rule.");
      return;
    }
  
    // Retrieve the selected rule from the rules array using the index
    const selectedRule = rules[selectedRuleIndex];
  
    // Extract the AST directly from the selected rule
    const ast = selectedRule.ast;
  
    // Prepare the evaluation data
    const evaluationData = {
      age,
      department,
      salary,
      experience
    };
  
    // Call the evaluateAST function with the AST and evaluation data
    const result = evaluateAST(ast, evaluationData);
  
    // Handle the evaluation result (e.g., display a message or update the UI)
    alert(`Evaluation Result: ${result ? "True" : "False"}`);
  };
  
  

  return (
    <section>
      <div className="container">
        <div className="row mt-5">
          <div className="col-lg-12 text-center fs-1 fw-bold text-dark">
            Rules & Eligibility
          </div>
        </div>
        <div className="row">
          <div className="col-lg-12 p-5 text-center">
            <div className="btn-group">
              <button
                className={`btn btn-outline-dark fw-bold ${activeTab === "create" ? "active" : ""}`}
                onClick={() => setActiveTab("create")}
              >
                Create Rule
              </button>
              <button
                className={`btn btn-outline-dark fw-bold ${activeTab === "combine" ? "active" : ""}`}
                onClick={() => {
                  setActiveTab("combine");
                  fetchRules();
                }}
              >
                Combine Rules
              </button>
              <button
                className={`btn btn-outline-dark fw-bold ${activeTab === "evaluate" ? "active" : ""}`}
                onClick={() => {
                  setActiveTab("evaluate");
                  fetchRules();
                }}
              >
                Evaluate Rules
              </button>
            </div>
          </div>
        </div>
        <div className="row mb-5">
          <div className="container p-5 feedback-box">
            <div className="row">
              <h3 className="text-center">
                {activeTab === "create" && "Create Rule"}
                {activeTab === "combine" && "Combine Rules"}
                {activeTab === "evaluate" && "Evaluate Rules"}
              </h3>
            </div>

            <div className="row">
              <div className="col-lg-3"></div>
              <div className="col-lg-6">
                {activeTab === "create" && (
                  <form>
                    <textarea
                      className={`form-control mb-3 ${errors.rule ? "is-invalid" : ""}`}
                      placeholder="Enter Rule"
                      value={rulegroup}
                      onChange={(e) => group(e.target.value)}
                      rows={8}
                      style={{ borderColor: errors.rule ? "red" : "" }}
                    ></textarea>
                    {errors.rule && <small className="text-danger fst-italic"><i>{errors.rule}</i></small>}
                    <div className='text-center'>
                      <button
                        type="button"
                        className="btn btn-primary mt-3"
                        onClick={validateRule}
                      >
                        Submit
                      </button>
                    </div>
                  </form>
                )}

                {activeTab === "combine" && (
                 <form>
                    <h5>Write rules and separate them by commas</h5>
                    <textarea
                      className={`form-control mb-3 ${errors.rule ? "is-invalid" : ""}`}
                      placeholder="Enter rules separated by commas"
                      value={rulegroup}
                      onChange={(e) => group(e.target.value)}
                      rows={8}
                      style={{ borderColor: errors.rule ? "red" : "" }}
                    ></textarea>
                    {grouperrors && <div className="text-danger">{grouperrors}</div>}
                    <div className="text-center">
                      <button
                        type="button"
                        className="btn btn-primary mt-3"
                        onClick={validate_combine_Rules}
                      >
                        Combine
                      </button>
                    </div>
               </form>
                )}

{activeTab === "evaluate" && (
                  <form>
                    <div className="mb-3">
                      <label className="form-label">Age</label>
                      <input
                        type="number"
                        className={`form-control ${errors.age ? "is-invalid" : ""}`}
                        value={age}
                        onChange={(e) => setAge(e.target.value)}
                        style={{ borderColor: errors.age ? "red" : "" }}
                      />
                      {errors.age && <small className="text-danger fst-italic"><i>{errors.age}</i></small>}
                    </div>
                    <div className="mb-3">
                      <label className="form-label">Department</label>
                      <input
                        type="text"
                        className={`form-control ${errors.department ? "is-invalid" : ""}`}
                        value={department}
                        onChange={(e) => setDepartment(e.target.value)}
                        style={{ borderColor: errors.department ? "red" : "" }}
                      />
                      {errors.department && <small className="text-danger fst-italic"><i>{errors.department}</i></small>}
                    </div>
                    <div className="mb-3">
                      <label className="form-label">Salary</label>
                      <input
                        type="number"
                        className={`form-control ${errors.salary ? "is-invalid" : ""}`}
                        value={salary}
                        onChange={(e) => setSalary(e.target.value)}
                        style={{ borderColor: errors.salary ? "red" : "" }}
                      />
                      {errors.salary && <small className="text-danger fst-italic"><i>{errors.salary}</i></small>}
                    </div>
                    <div className="mb-3">
                      <label className="form-label">Experience</label>
                      <input
                        type="number"
                        className={`form-control ${errors.experience ? "is-invalid" : ""}`}
                        value={experience}
                        onChange={(e) => setExperience(e.target.value)}
                        style={{ borderColor: errors.experience ? "red" : "" }}
                      />
                      {errors.experience && <small className="text-danger fst-italic"><i>{errors.experience}</i></small>}
                    </div>

                    {rules.length > 0 && (
                      <div>
                        <label className="form-label">Select the rule by which you want to evaluate:</label>
                        {rules.map((rule, index) => (
                        <div key={index} className="form-check">
                          <input
                            className="form-check-input"
                            type="radio"
                            value={index}
                            checked={selectedRuleIndex === index}
                            onChange={() => handleRuleSelection(index)}
                          />
                          <label className="form-check-label">
                            {rule.rule}
                          </label>
                        </div>
                      ))}

                        {errors.selectedRule && <small className="text-danger fst-italic"><i>{errors.selectedRule}</i></small>}
                      </div>
                    )}
                    <div className='text-center'>
                      <button
                        type="button"
                        className="btn btn-primary mt-3 bg-dark"
                        onClick={validateEvaluation}
                      >
                        Evaluate
                      </button>
                    </div>
                  </form>
                )}
              </div>
              <div className="col-lg-3"></div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Dashboard;
