import {Link}  from 'react-router-dom';
import { useState, useEffect } from 'react';



const Viewfeedback = () =>{
    let[isHidden, setIsHidden] = useState(true);
    let [reviews, updatecategory] = useState([]);


    const getAllReview = () => {
        fetch("http://localhost:1234/productlist")
          .then((response) => response.json())
          .then((reviewarray) => {
            updatecategory(reviewarray.reverse());
          });
      };

      const getfeature = () => {
        fetch("http://localhost:1234/productlist")
          .then((response) => response.json())
          .then((reviewarray) => {
            updatecategory(reviewarray.reverse());
          });
      };

      const getpricing = () => {
        fetch("http://localhost:1234/productlist")
          .then((response) => response.json())
          .then((reviewarray) => {
            updatecategory(reviewarray.reverse());
          });
      };

      const getusability = () => {
        fetch("http://localhost:1234/productlist")
          .then((response) => response.json())
          .then((reviewarray) => {
            updatecategory(reviewarray.reverse());
          });
      };
    
      useEffect(() => {
        getAllReview();
      }, [1]);

      
    return(
    <section>
          <nav className="navbar bg-body-tertiary bg-dark  p-4 h1">
          <div className="container-fluid">
         <span className="navbar-brand mb-0 h1">Feedback Portal</span>
           {/* hide the button onclick */}
          <Link to="/Addfeedback" className="menu"> {isHidden && <button className="btn btn-primary" type="submit" onClick={() => setIsHidden(false)}>Add Feedback</button>} </Link>
      </div>
       </nav>

        <div class="container "> 
             <div className="row mt-5">
                <div className="col-lg-12 text-center fs-1 text-warning menu"> Feedbacks </div>
             </div>

            <div className="row "> 
                        <div className="col-lg-12 p-5 text-center">
                           <div className="btn btn-group">
                          <button className="btn btn-primary text-white fs-6 " style = {{ padding: '4px 15px 4px' }} onClick={getAllReview} >All</button> 
                            <button className="btn btn-info text-white fs-6" style = {{ padding: '4px 15px 4px' }} onClick={getfeature}>Features</button> 
                            <button className="btn btn-success text-white fs-6" style = {{ padding: '4px 15px 4px' }} onClick={getpricing}>Pricing</button> 
                           <button className="btn btn-warning text-white fs-6" style = {{ padding: '4px 15px 4px' }} onClick={getusability}>Usability</button> 
                          </div>
                          </div>
             </div>

            
        <div className="row">
             <div className='container p-5 bg-light rounded'>
       {
           reviews.map((review, index) => {
            return (
            //   <div className="col-lg-3 mb-4" key={index}>
            //     <div className="p-3 text-center shadow">
            //       <h4 className="text-info mb-3"> {review.name} </h4>
            //       <img
            //         src={review.photo}
            //         className="rounded"
            //         height="140"
            //         width="100%"
            //       />
            //       <p className="mt-3"> {review.details} </p>
            //       <p className="m-3">Rs. {review.price} </p>
            //     </div>
            //   </div>
            <div className="col-lg-8">
            <h3 className="text-center">
                Items in Cart : {allproduct.length}
            </h3>

            <table className="table table-bordered shadow">
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Total</th>
                        <th>Photo</th>
                        <th className="text-center">Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                         allproduct.map((product, index)=>{
                            total = total + product.price * product.qty;
                           return(
                            <tr key={index}>
                                <td>{product.name} </td>
                                <td>{product.price}</td>
                                <td className="input-group"> 
                                <button className="btn btn-info btn-sm" onClick={changeQty.bind(this, product, "A")}> <i className="fa fa-plus"></i></button>
                                <input type="text" className="form-control" value={product.qty} readOnly size= "1"/>
                                <button className="btn btn-warning  btn-sm" onClick={changeQty.bind(this, product, "B")}><i className="fa fa-minus" ></i></button>
                                 </td>
                                <td>{ product.qty * product.price}</td>
                                <td><img src={product.photo}  height="50" width="50" /></td>

                                <td className="text-center">
                                    <i className="fa fa-2x fa-trash fa-lg text-danger" onClick={delitem.bind(this, product.id, product.name)}></i>
                                </td>

                            </tr>
                         
                           )
                         })
                        }
                         <tr>
                         <td colspan="6" className="text-center text-danger fs-3">Total Amount - {total} </td>
                         </tr>
                   
                </tbody>
            </table>
        </div>

            );
          })
    
      }
      </div> {/* container end*/}
        </div>  {/* row end*/}
      </div>   {/* container end*/}

</section>
    )
}

export default Viewfeedback;
